export declare const typeDefs = "#graphql\n  # =============================================================================\n  # DIMENSION TYPES (mirror DB table names and columns)\n  # =============================================================================\n\n  type DimProduct {\n    product_key: Int!\n    vin_productid: String\n    product_name: String\n    product_type: String\n  }\n\n  type DimDisease {\n    disease_key: Int!\n    vin_diseaseid: String\n    disease_name: String\n    global_health_area: String\n    disease_type: String\n  }\n\n  type DimPhase {\n    phase_key: Int!\n    vin_rdstageid: String\n    phase_name: String\n    sort_order: Int\n  }\n\n  type DimGeography {\n    country_key: Int!\n    vin_countryid: String\n    country_name: String\n    iso_code: String\n    region_name: String\n  }\n\n  type DimOrganization {\n    organization_key: Int!\n    accountid: String\n    org_name: String\n    org_type: String\n  }\n\n  type DimPriority {\n    priority_key: Int!\n    vin_rdpriorityid: String\n    priority_name: String\n    indication: String\n    intended_use: String\n  }\n\n  type DimDate {\n    date_key: Int!\n    full_date: String\n    year: Int\n    quarter: Int\n  }\n\n  type DimCandidateTech {\n    technology_key: Int!\n    platform: String\n    technology_type: String\n    molecule_type: String\n    route_of_admin: String\n  }\n\n  type DimCandidateRegulatory {\n    regulatory_key: Int!\n    approval_status: String\n    sra_approval_flag: Int\n    fda_approval_date: String\n    who_prequal_date: String\n  }\n\n  type DimDeveloper {\n    developer_key: Int!\n    developer_name: String\n  }\n\n  type DimCandidateCore {\n    candidate_key: Int!\n    vin_candidateid: String\n    candidate_name: String\n    vin_candidate_code: String\n    developers_agg: String\n\n    # Resolved via joins\n    disease: DimDisease\n    phase: DimPhase\n    product: DimProduct\n    developers: [DimDeveloper!]!\n    geographies: [CandidateGeography!]!\n    priorities: [DimPriority!]!\n    clinicalTrials: [FactClinicalTrialEvent!]!\n  }\n\n  # =============================================================================\n  # FACT TYPES\n  # =============================================================================\n\n  type FactClinicalTrialEvent {\n    trial_id: Int!\n    candidate_key: Int\n    start_date_key: Int\n    trial_phase: String\n    enrollment_count: Int\n    status: String\n  }\n\n  # =============================================================================\n  # AGGREGATE TYPES (dashboard KPIs and visualizations)\n  # =============================================================================\n\n  type PortfolioKPIs {\n    totalDiseases: Int!\n    totalCandidates: Int!\n    approvedProducts: Int!\n  }\n\n  type GlobalHealthAreaSummary {\n    global_health_area: String!\n    candidateCount: Int!\n    diseaseCount: Int!\n    productCount: Int!\n  }\n\n  type PhaseDistributionRow {\n    global_health_area: String!\n    phase_name: String!\n    sort_order: Int!\n    candidateCount: Int!\n  }\n\n  type GeographicDistributionRow {\n    country_key: Int!\n    country_name: String!\n    iso_code: String\n    location_scope: String!\n    candidateCount: Int!\n  }\n\n  type TemporalSnapshotRow {\n    year: Int!\n    phase_name: String!\n    sort_order: Int!\n    candidateCount: Int!\n  }\n\n  # =============================================================================\n  # CONNECTION TYPES (pagination)\n  # =============================================================================\n\n  type CandidateConnection {\n    nodes: [DimCandidateCore!]!\n    totalCount: Int!\n    hasNextPage: Boolean!\n  }\n\n  type CandidateGeography {\n    country_key: Int!\n    country_name: String\n    iso_code: String\n    location_scope: String\n  }\n\n  # =============================================================================\n  # INPUT TYPES\n  # =============================================================================\n\n  input CandidateFilter {\n    global_health_area: String\n    disease_key: Int\n    product_key: Int\n    phase_key: Int\n    year: Int\n    is_active: Boolean\n  }\n\n  # =============================================================================\n  # QUERY ROOT\n  # =============================================================================\n\n  type Query {\n    # KPIs (3 homepage cards)\n    portfolioKPIs: PortfolioKPIs!\n\n    # Bubble chart\n    globalHealthAreaSummaries: [GlobalHealthAreaSummary!]!\n\n    # Stacked bar chart\n    phaseDistribution(global_health_area: String, product_key: Int): [PhaseDistributionRow!]!\n\n    # Map\n    geographicDistribution(location_scope: String!): [GeographicDistributionRow!]!\n\n    # Cross-pipeline temporal\n    temporalSnapshots(years: [Int!], disease_key: Int): [TemporalSnapshotRow!]!\n\n    # Lists with pagination\n    candidates(filter: CandidateFilter, limit: Int, offset: Int): CandidateConnection!\n\n    # Detail\n    candidate(candidate_key: Int!): DimCandidateCore\n\n    # Filter dropdowns (lookups)\n    diseases: [DimDisease!]!\n    phases: [DimPhase!]!\n    products: [DimProduct!]!\n    countries: [DimGeography!]!\n    availableYears: [Int!]!\n    locationScopes: [String!]!\n  }\n";
